
  def suma(x, y) 
    puts "la suma es igual a #{x+y.to_i}" 
  end

  def resta(x, y)
    puts "la resta es igual a #{x-y.to_i}"
  end

  def mult(x, y)
    puts "la multiplicacion es igual a #{x*y.to_i}"
  end

  def div(x, y)
    puts "la divicion es igual a #{x/y.to_f}"
  end

suma(10,20)
resta(10,20)
mult(10,20)
div(10,20)
